import { Component, Input , Output, EventEmitter } from '@angular/core';
import { IProduct } from '../catalog/product.model'; // Chemin correct


@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent {

  @Input() product!:IProduct;
  @Output() buy= new EventEmitter();


  getImageUrl(product: IProduct) {
    if (!product) return '';
    return '/assets/images/robot-parts/' + product.imageName;
  }

  addTocart(product: IProduct){

    console.log('product ${product.name} added to cart');
  }
  buyButtonClicked(product:IProduct){
    this.buy.emit();
  }

}
<div class="product">
  <div class="product-details">
    <img [src]="getImageUrl(product)" [alt]="product.name" />
    <div class="product-info">
      <div class="name">{{ product.name }}</div>
      <div class="description">{{ product.description }}</div>
      <div class="category">Part Type: {{ product.category }}</div>
    </div>
  </div>
  <div class="price">
    <div *ngIf="product.discount === 0">
      <span class="current-price">{{ product.price | currency }}</span>
    </div>
    <div *ngIf="product.discount > 0" class="discount">
      <span class="original-price" style="text-decoration: line-through;">{{ product.price | currency }}</span>
      <span class="current-price">{{ product.price * (1 - product.discount) | currency }}</span>
    </div>
    <button class="cta" (click)="buyButtonClicked(product)">Buy</button>
  </div>
</div>
